/* New cleaned up version of App.js */
import React from 'react';

// Importing react-router-dom to use the React Router
import './App.css';
import Header from './react-components/Header';
import SearchBox from './react-components/SearchBox';
import ItemPage from './react-components/ItemPage';
import { Route, Switch, BrowserRouter, Link } from 'react-router-dom';


class App extends React.Component {

  // a 'global' state that you can pass through to any child componenets of App.
  //   In the Routes below they are passed to both the Home and Queue states.
  state = {
    searchInput : ""
  }

  handleInputChange = (event) => {
    event.preventDefault();
    const target = event.target;
    const value = target.value;
    const name = target.name;

    // 'this' is bound to the component in this arrow function.
    this.setState({
      [name]: value // [name] sets the object property name to the value of the 'name' variable.
    });

  };

  handleButtonChange = (event) => {
    event.preventDefault()
    console.dir(this.state.searchInput)
    //link to search page
  };

  render() {
    document.title = "October's Very Oction";
    return (
      <div> 
        <Header/>
        <div id="mainTextDiv">
          Buy & Sell <br/>
          Authenticate Sneakers
        </div>
        <SearchBox 
          handleInputChange = {this.handleInputChange}
          handleButtonChange = {this.handleButtonChange}
        />
        <BrowserRouter>
          <Switch> { /* Similar to a switch statement - shows the component depending on the URL path */ }
            { /* Each Route below shows a different component depending on the exact path in the URL  */ }
            <Route exact path='/ItemPage' render={() => 
                            (<ItemPage state={this.state}/>)} />

          </Switch>
        </BrowserRouter>


      </div>
    );  
  }
}

export default App;
